import React from 'react';
import styled, { keyframes } from 'styled-components';

const slideInRight = keyframes`
  0% {
    transform: translateX(100%);
    opacity: 0;
  }
  100% {
    transform: translateX(0);
    opacity: 1;
  }
`;

const ExperienceContainer = styled.div`
  width: 100%;  // Takes the full width of parent
  display: flex;
  flex-direction: column;
  align-items: center;  // Centered items
  animation: 1s ${slideInRight} ease-out;
  background: rgba(255, 255, 255, 0.05);
  
  h1 {
    color: #90C8AC;
    margin-bottom: 1rem;
    width: 100%;  // Takes the full width of container
    text-align: left;  // Aligns text to the left
  }
`;

const Timeline = styled.div`
  position: relative;
  width: 100%;  // Takes the full width of container
`;

const TimelineItem = styled.div`
  padding: 1rem;
  margin-bottom: 2rem;
  position: relative;
  border-radius: 6px;
  background-color: transparent;
  
  &:not(:last-child):after { // Divider line between items but not after the last item
    content: "";
    display: block;
    width: 300px;  // Centered line with a length of 300px
    height: 1px;
    background-color: #C4DFAA;
    margin: 20px auto 0;
  }
  
  h4 {
    font-weight: bold;
    color: #3498db;
  }
  span {
    font-size: 0.75rem;
    margin-bottom: 0.5rem;
    display: block;
    color: #888;
  }
  p {
    font-size: 0.85rem;
  }
`;

const Experience = () => {
  return (
    <ExperienceContainer>
      <h1>Experience</h1>
      <Timeline>
        <TimelineItem>
          <h4>Software Engineer at Google</h4>
          <span>Jun 2022 - Present</span>
          <p>Worked on developing new features and maintaining Google Search.</p>
        </TimelineItem>
        <TimelineItem>
          <h4>Software Engineer Intern at Microsoft</h4>
          <span>May 2021 - Aug 2021</span>
          <p>Worked on the Microsoft Teams team, developing new features and fixing bugs.</p>
        </TimelineItem>
      </Timeline>
    </ExperienceContainer>
  );
};

export default Experience;
