import React from 'react';
import styled from 'styled-components';
import { Link } from 'react-scroll';
import { SocialIcon } from 'react-social-icons';

const Name = styled.h1`
  font-family: 'Cinzel Decorative', cursive;
  font-weight: 700;
  font-size: 20px;
  color: #EEEEEE;
  margin-bottom: 1rem;
  text-align: center;
`;

const JobTitle = styled.h2`
  font-family: 'Gruppo', sans-serif;
  font-size: 15px;
  color: #DCDCDC;
  margin-bottom: 2rem;
  margin-left: 30px;
`;

const SidebarContainer = styled.div`
  background-color: #032D3C;
  color: #DEE1E6;
  width: 350px;
  height: 100vh;
  display: flex;
  flex-direction: column;
  align-items: center;  // center align-items
  overflow: visible;
`;

const ProfileImage = styled.img`
  width: 200px;
  height: 200px;
  border-radius: 50%;
  object-fit: cover;
  margin-bottom: 1rem;
`;

const StyledLink = styled(Link)`
  font-family: 'Orbitron', sans-serif;
  font-weight: 600;
  font-size: 13px;  // changed font size to 13px
  color: #F1F3F4;  // reversed color
  text-decoration: none;
  margin: 0.5rem 0;
  margin-left: 30px;
  cursor: pointer;
  position: relative;
  display: inline-block;

  span {
    content: '';
    position: absolute;
    width: 0;
    height: 2px;
    bottom: -2px;
    left: 0;
    background-color: #B7B78A;  // reversed color
    transition: all 0.3s ease-in-out;
  }

  &:hover {
    color: #B7B78A;  // reversed color
  }

  &:hover span {
    width: 100%;
  }
`;

const SocialMediaContainer = styled.div`
  display: flex;
  justify-content: space-around;
  width: 100%;
  position: absolute;
  bottom: 2rem;
`;

const Sidebar = () => {
  return (
    <SidebarContainer>
      <ProfileImage src="path-to-your-image.jpg" alt="Profile" />
      <Name>Your Name</Name>
      <JobTitle>Your Job Title</JobTitle>
      <StyledLink to="experience" smooth={true} duration={500}>
        Experience
        <span />
      </StyledLink>
      <StyledLink to="projects" smooth={true} duration={500}>
        Projects
        <span />
      </StyledLink>
      <StyledLink to="skills" smooth={true} duration={500}>
        Skills
        <span />
      </StyledLink>
      <StyledLink to="contact" smooth={true} duration={500}>
        Contact
        <span />
      </StyledLink>
      <SocialMediaContainer>
        <SocialIcon url="http://linkedin.com/in/yourlinkedinprofile" bgColor="#DEE1E6" />
        <SocialIcon url="http://github.com/yourgithubprofile" bgColor="#DEE1E6" />
        <SocialIcon url="http://twitter.com/yourtwitterhandle" bgColor="#DEE1E6" />
      </SocialMediaContainer>
    </SidebarContainer>
  );
};

export default Sidebar;
