import React from 'react';
import styled ,{keyframes}from 'styled-components';
import { Link } from 'react-scroll';
import { SocialIcon } from 'react-social-icons';

// Import your image
import profileImage from './IMG_0178__1_-removebg-preview.jpg';
import  Cursor  from './Cursor';
import { GradientBackground } from './StyledComp';



const Sidebar = (props) => {
  return (
    <SidebarContainer backgroundColor={props.backgroundColor} borderColor="#0C356A">
    <GradientWrapper startColor="#5F7161" endColor="#6D8B74">
      <ProfileImage src={profileImage} alt="Your Name" />

      <Name scrollPercentage={props.scrollPercentage}>SAAD KATAR</Name>
      <JobTitle scrollPercentage={props.scrollPercentage}>JUNIOR SOFTWARE ENGINEER</JobTitle>

      <NavContainer>
      <GlowingLine width="100%" height="1px" />

        <StyledLink to="experience" smooth={true} duration={500}>
        <span></span> {/* This span is responsible for the line animation */}

          <GlowsText color="#DEE1E6">EXPERIENCE</GlowsText>
        </StyledLink>

        <Spacer height="10px" />

        <StyledLink to="projects" smooth={true} duration={500}>
          <GlowsText color="#DEE1E6">PROJECTS</GlowsText>
          <span></span> {/* This span is responsible for the line animation */}

        </StyledLink>

        <Spacer height="10px" />

        <StyledLink to="skills" smooth={true} duration={500}>
          <GlowsText color="#DEE1E6">SKILLS</GlowsText>
          <span></span> {/* This span is responsible for the line animation */}

        </StyledLink>

        <Spacer height="10px" />

        <StyledLink to="contact" smooth={true} duration={500}>
          <GlowsText color="#DEE1E6">CONTACT</GlowsText>
          <span></span> {/* This span is responsible for the line animation */}

        </StyledLink>
      </NavContainer>

      <GlowingLine width="100%" height="1px" />

      <SocialMediaContainer>  
        <GlowingLogo url="http://linkedin.com/in/yourlinkedinprofile" bgColor="#DEE1E6" />
        <Spacer width="10px" />
        <GlowingLogo url="http://github.com/yourgithubprofile" bgColor="#DEE1E6" />
        <Spacer width="10px" />
        <GlowingLogo url="http://twitter.com/yourtwitterhandle" bgColor="#DEE1E6" />
      </SocialMediaContainer>
    </GradientWrapper>

    <FixedLogoContainer>
      <GlowingLogo src={profileImage} size="30px" alt="Your Name" />
    </FixedLogoContainer>
  </SidebarContainer>
  );
};

export default Sidebar;



// Animations
const glowingAnimation = keyframes`
  0% {
    box-shadow: 0 0 1px #00ff00;
  }
  50% {
    box-shadow: 0 0 20px #00ff00, 0 0 3px #00ff00;
  }
  100% {
    box-shadow: 0 0 1px #00ff00;
  }
`;
const glowingBorderAnimation = keyframes`
  0% {
    border-color: #00ff00;
    box-shadow: 0 0 1px #00ff00;
  }
  50% {
    border-color: #00ff00;
    box-shadow: 0 0 20px #00ff00, 0 0 3px #00ff00;
  }
  100% {
    border-color: #00ff00;
    box-shadow: 0 0 1px #00ff00;
  }
`;
const glowingTextAnimation = keyframes`
  0% {
    text-shadow: 0 0 2px #00ff00;
  }
  50% {
    text-shadow: 0 0 4px #00ff00, 0 0 2px #00ff00;
  }
  100% {
    text-shadow: 0 0 2px #00ff00;
  }
`;

// Sidebar Container with glowing border


// Glowing Text Component
const GlowsText = styled.div`
  font-size: 16px;
  color: ${(props) => props.color || '#00ff00'};
  animation: ${glowingTextAnimation} 1.5s infinite;
`;

// Glowing Line (Horizontal & Vertical) Components
const GlowingLine = styled.div`
  width: ${(props) => props.width || '100%'};
  height: ${(props) => props.height || '1px'};
  margin-bottom:20px;
  background-color: #00ff00;
  animation: ${glowingAnimation} 1.5s infinite;
`;

// Fixed Logos at the Bottom of the Page
const FixedLogoContainer = styled.div`
  position: fixed;
  bottom: 10px;
  left: 50%;
  transform: translateX(-50%);
`;

const GlowingLogo = styled.img`
  width: ${(props) => props.size || '50px'};
  animation: ${glowingAnimation} 1.5s infinite;
`;

// Text Alignment Component
const TextAlign = styled.div`
  text-align: ${(props) => props.align || 'left'};
`;

// Gradient Background Wrapper
const GradientWrapper = styled.div`
height:100%;
  background: linear-gradient(${(props) => props.direction || 'to right'}, ${props => props.startColor || '#5F7161'}, ${props => props.endColor || '#6D8B74'});
  padding: ${(props) => props.padding || '0'};
  margin:0;
`;

// Generic Container
const Container = styled.div`
  padding: ${(props) => props.padding || '20px'};
  margin: ${(props) => props.margin || '0'};
  border: ${(props) => props.border || 'none'};
`;

// Spacer Component
const Spacer = styled.div`
  height: ${(props) => props.height || '20px'};
  width: ${(props) => props.width || '20px'};
`;

// Button Component
const Button = styled.button`
  padding: ${(props) => props.padding || '10px 20px'};
  background-color: ${(props) => props.bgColor || '#00ff00'};
  color: ${(props) => props.color || '#fff'};
  border: none;
  border-radius: ${(props) => props.borderRadius || '5px'};
`;

// Icon Component
const Icon = styled.i`
  color: ${(props) => props.color || '#00ff00'};
  font-size: ${(props) => props.size || '16px'};
`;

// Tooltip Component
const Tooltip = styled.span`
  visibility: hidden;
  background-color: #555;
  color: #fff;
  text-align: center;
  padding: 8px 0;
  position: absolute;
  z-index: 1;
  bottom: 125%; 
  left: 50%; 
  margin-left: -60px; 
  opacity: 0;
  transition: opacity 0.3s;

  &:hover {
    visibility: visible;
    opacity: 1;
  }
`;

const Name = styled.h1`
  font-family: 'Tiller', italic;
  font-weight: 700;
  font-size: 20px;
  color: ${props => `rgb(${Math.round(238 - 34 * props.scrollPercentage)}, ${Math.round(238 - 34 * props.scrollPercentage)}, ${Math.round(238 - 34 * props.scrollPercentage)})`};
  margin-bottom: 1rem;
  margin-left: 3px;
`;

const JobTitle = styled.h2`
  font-family: 'Gruppo', sans-serif;
  font-size: 10px;
  color: ${props => `rgb(${Math.round(238 - 34 * props.scrollPercentage)}, ${Math.round(238 - 34 * props.scrollPercentage)}, ${Math.round(238 - 34 * props.scrollPercentage)})`};
  margin-bottom: 2rem;
  margin-left: 3px;
`;

const SidebarContainer = styled.div`
  position: relative;
  background:  #0B666A;
  height: 100vh; // This ensures it covers the entire viewport height


   width: 150px;
   min-width:150px;
   min-height:100%;
  display: flex;
  flex-direction: column;
  overflow: visible;
  padding: 0;
  margin:0;
  border-right: ${(props) => `3px solid ${props.borderColor || 'red'}`};
  animation: ${glowingBorderAnimation} 1.5s infinite;

`;



const ProfileImage = styled.img`
  width: 50px;
  height: 50px;
  border-radius: 50%;
  object-fit: cover;
  margin-bottom: 1rem;
  margin-top:20px;

`;

const WholeApp = styled.div`
  transition: 0.3s;
  &:hover {
    filter: brightness(1.1) blur(5px);
  }
`;
const StyledLink = styled(Link)`
  font-family: 'Orbitron', sans-serif;
  font-weight: 600;
  font-size: 15px;
  color: #BABCBE;
  text-decoration: none;
 margin-left: 5px;
  cursor: pointer;
  position: relative;
  display: inline-block;
  margin-bottom:20px;

  span {
    content: '';
    position: absolute;
    width: 0;
    height: 2px;
    bottom: -2px;
    left: 0;
    background-color: #B7B78A;
    transition: all 0.3s ease-in-out;
  }

  &:hover {
    color: #FAEEE0;
  }

  &:hover span {
    width: 100%;!important
  }
`;

const SocialMediaContainer = styled.div`
  display: flex;
  justify-content: space-around;
  width: 100%;
  padding-top: 200px;

  bottom: 2rem;
    margin-top: 200px;  // This pushes the container to the bottom of its parent

`;
const NavContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  width: 100%;
  margin-top:100px;
`;
