import React , {useState,useEffect}from 'react';
import { Button } from '@mui/material';
import Typist from 'react-typist';
import Particles from 'react-tsparticles';
import ScrollIntoView from 'react-scroll-into-view';
import profilePic from './IMG_0178__1_-removebg-preview.jpg';
import About from './About';
import Skills from './Skills';
import CardsList from"./CardsList";

import Projects from './Projects';
import styled, { keyframes } from 'styled-components';
import Blog from './Blog';
import Contact from './Contact';
import Experience from './Experience';
import { useViewportScroll, useTransform, m as motion,useAnimation } from 'framer-motion';
import { useInView } from 'react-intersection-observer';
import ReactTypingEffect from 'react-typing-effect';
import {Typewriter} from "react-simple-typewriter"
import { GradientBackground } from './StyledComp';

import ArrowIcon from "./239-right-arrow.png"

import { Link, Element } from 'react-scroll';

// Your styled components here

const HomePage = () => {
  const [scrollDirection, setScrollDirection] = useState(null);
  
  useEffect(() => {
    let lastScrollY = window.pageYOffset;
    const onScroll = () => {
      const scrollY = window.pageYOffset;
      setScrollDirection(scrollY > lastScrollY ? 'down' : 'up');
      lastScrollY = scrollY;
    }
    window.addEventListener('scroll', onScroll);
    return () => window.removeEventListener('scroll', onScroll);
  }, []);

  const topRef = React.useRef();
  const bottomRef = React.useRef();
  
  React.useEffect(() => {
    const observer = new IntersectionObserver(
      entries => {
        entries.forEach(entry => {
          if (entry.isIntersecting) {
            setScrollDirection(entry.target === topRef.current ? 'up' : 'down');
          }
        });
      },
      {
        threshold: 0.1,
      }
    );
  
    if(topRef.current) {
      observer.observe(topRef.current);
  }
  if(bottomRef.current) {
      observer.observe(bottomRef.current);
  }
  
    return () => {
      observer.disconnect();
    };
  }, []);
  const { scrollYProgress } = useViewportScroll();
  const scale = useTransform(scrollYProgress, [0, 1], [1, 1.5]);
  const rotate = useTransform(scrollYProgress, [0, 1], [0, 360]);
  const animation = useAnimation();
  const [ref, inView] = useInView({
    threshold: 0.1,
  });

  React.useEffect(() => {
    if (inView) {
      animation.start({
        opacity: 1,
        transition: {
          duration: 0.5,
        },
      });
    }
    if (!inView) {
      animation.start({
        opacity: 0,
      });
    }
  }, [animation, inView]);


  return (
    <HomePageContainer>
      <GradientBackground startColor="#FF0000" endColor="#00FF00" direction="to bottom">

      <Fade direction="bottom" top opacity={scrollDirection === 'down' ? 0 : 1} ref={topRef} />

     
     
      <Hero>
       
      </Hero>
      <motion.div style={{ scale, rotate }}>
      <motion.div ref={ref} animate={animation}>
      <StyledElement name="about" title="About">
      <img src={ArrowIcon} alt="Arrow Icon" style={{ position: 'absolute', left: '0', top: '50%', transform: 'translateY(-50%)', width: '40px', height: '40px' }} />
      
    
</StyledElement>
<About />
      </motion.div>
      <StyledElement name="Skills" title="SKILLS">
      <img src={ArrowIcon} alt="Arrow Icon" style={{ position: 'absolute', left: '0', top: '50%', transform: 'translateY(-50%)', width: '40px', height: '40px' }} />
      
    
</StyledElement>

        <Element name="skills">

          <Skills />
        </Element>
        <StyledElement name="Project" title="PROJECT">
      <img src={ArrowIcon} alt="Arrow Icon" style={{ position: 'absolute', left: '0', top: '50%', transform: 'translateY(-50%)', width: '40px', height: '40px' }} />
      
    
</StyledElement>

        <Element name="projects">
          <Projects />
        </Element>
      </motion.div>
      <StyledElement name="Project" title="Experience">
      <img src={ArrowIcon} alt="Arrow Icon" style={{ position: 'absolute', left: '0', top: '50%', transform: 'translateY(-50%)', width: '40px', height: '40px' }} />
      
    
</StyledElement>
      <CardsList/>
    
      <Element name="experience">
        <Experience />
      </Element>
      <Element name="contact">
        <Contact />
      </Element>
      <Fade direction="bottom" top opacity={scrollDirection === 'down' ? 0 : 1} ref={topRef} />
  </GradientBackground>
    </HomePageContainer>
  );
};

export default HomePage;

const fade = keyframes`
  from { opacity: 0; }
  to { opacity: 1; }
`;

// Define the styled components
const HomePageContainer = styled.div`
  position: relative;
  display: flex;
  align-items: flex-start; 
  justify-content: flex-start;
  flex-direction: column;
  min-height: 100vh;
  background: rgb(0, 0, 34);
  color: white;
  font-family: 'Roboto', sans-serif;
  overflow: hidden;

  &::before {
    content: ""; // Empty content for the pseudo-element
    position: absolute;
    left: 0;
    top: 37px;
    
    bottom: 0; // Extends from top to bottom of the container
    width: 2px; // Width of the vertical line
    background-color: #FFF3B7; // Color of the vertical line, change it as required
  }
`;


const Fade = styled.div`
  position: absolute;
  top: ${props => props.top ? '0' : 'auto'};
  bottom: ${props => props.bottom ? '0' : 'auto'};
  height: 100px;
  pointer-events: none;
  background: linear-gradient(
    to ${props => props.direction}, 
    rgba(0, 0, 34, 1),
    rgba(0, 0, 34, 0)
  );
  transition: opacity 0.5s ease;
  opacity: ${props => props.opacity};
`;

const Header = styled.header`
  position: sticky;
  top: 0;
  margin-top: 5px;
  width: 100%;
  display: flex;
  justify-content: center;
  padding: 1rem 0;
  background: rgba(255, 255, 255, 0.05);
  backdrop-filter: blur(5px);
  z-index: 100;
  border-radius: 10px;
  nav {
    display: flex;
    justify-content: space-between;
    width: 50%;
  }
  a {
    color: black;
    text-decoration: none;
    font-size: 1.5em;
    letter-spacing: 2px;
    transition: color 0.3s;
    &:hover {
      color: #3498db;
    }
  }
`;

const slideInLeft = keyframes`
  from { transform: translateX(-100%); }
  to { transform: translateX(0); }
`;

const slideInRight = keyframes`
  from { transform: translateX(100%); }
  to { transform: translateX(0); }
`;

const StyledButton = styled(Button)`
  && {
    background: white;
    color: #3498db !important;
    text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.5);
    &:hover {
      background: #2980b9 !important;
    }
  }
`;

const Hero = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-top: 5px;
  width: 80%;
  div {
    width: 40%;
    animation: 2s ${slideInLeft} ease-out;
  }
  
  h1,
  h2,
  p {
    margin-bottom: 20px;
    animation: 2s ${fade} ease-in;
  }
  h1 {
    font-size: 3em;
    text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.5);
  }
  h2,
  p {
    font-size: 2em;
  }
`;

const Footer = styled.footer`
  position: fixed;
  bottom: 0;
  width: 100%;
  display: flex;
  justify-content: center;
  padding: 10px 0;
  background: rgba(0, 0, 0, 0.8);
  backdrop-filter: blur(5px);
`;
const Section = styled.div`
  background: #ffffff;
  color: #333;
  padding: 2em 0;
  box-shadow: 0px 10px 15px -3px rgba(0, 0, 0, 0.1),
              0px 4px 6px -2px rgba(0, 0, 0, 0.05);
  &:nth-child(even) {
    background: #f0f0f0;
  }
`;
const StyledElement = styled(Element)`
    position: relative;
    padding-left: 3.5rem; // Adjust padding to make space for the icon
    font-size:25px;
    &::after {
        content: '${props => props.title}';
        position: absolute;
        left: 30px; // Adjust this value based on the icon's width
        top: 50%;
        transform: translateY(-50%);
        white-space: nowrap;
        color: #FFF3B7;
        padding: 0.2rem 0.5rem;
    }
`;
