import React from 'react';
import styled from 'styled-components';
import { Card, CardHeader, CardContent, Typography, LinearProgress } from '@mui/material';

const SkillsContainer = styled.div`
  display: flex;
  flex-wrap: wrap;
  justify-content: space-around;
`;
const StyledCard = styled(Card)`
  && {
    max-width: 645px;
    margin: 16px;
    width: 500px;

    background: rgba(255, 255, 255, 0.02);
    padding: 10px; // You can adjust this as needed
    border-radius: 5px;
    color: #BDC7C9  ;

    hover {
   
      backdrop-filter: blur(10px);
  
      background-color: rgba(255, 255, 255, 0.1);  // Increase the alpha value for a clearer background
    }
    .MuiCardHeader-title {
      font-size: 1.5em;
      font-weight: bold;
      color: #0a3d62; /* Deep Blue */
      padding: 8px 0;
    }

    .MuiCardHeader-subheader {
      img {
        width: 50px;
        height: 50px;
      }
    }

    .MuiCardContent-root {
      color: #0a3d62;
    }
  }
`;


const SkillCard = ({ title, logoSrc, skills }) => (
  <StyledCard>
    <CardHeader title={title} subheader={<img src={logoSrc || "https://via.placeholder.com/50"} alt={title} />} />
    <CardContent>
      {skills.map(skill => (
        <div key={skill.name}>
          <Typography variant="body2" color="text.secondary">
            {skill.name}
          </Typography>
          <LinearProgress variant="determinate" value={skill.level} />
        </div>
      ))}
    </CardContent>
  </StyledCard>
);


const Skills = () => {
  const skillsData = [
    {
      title: "Frontend",
      logoSrc: "frontend_logo.png",
      skills: [
        { name: "HTML", level: 90 },
        { name: "CSS", level: 85 },
        { name: "JavaScript", level: 80 },
        // More skills...
      ],
    },
    {
      title: "Backend",
      logoSrc: "backend_logo.png",
      skills: [
        { name: "Node.js", level: 80 },
        { name: "Python", level: 75 },
        // More skills...
      ],
    },
    // More skill categories...
  ];

  return (
    <SkillsContainer>
      {skillsData.map((data, index) => (
        <SkillCard key={index} {...data} />
      ))}
    </SkillsContainer>
  );
};

export default Skills;
