// 27. Radial Progress Bar: A circular progress indicator
export const RadialProgressBar = styled.div`
  width: 100px;
  height: 100px;
  border-radius: 50%;
  border: 5px solid #e0e0e0;
  position: relative;

  &::after {
    content: "";
    position: absolute;
    top: 5px;
    left: 5px;
    right: 5px;
    bottom: 5px;
    border-radius: 50%;
    background: #fff;
    z-index: 1;
  }

  div {
    position: absolute;
    width: 50%;
    height: 100%;
    overflow: hidden;
    top: 0;

    &:first-child {
      left: 0;
      transform: rotate(${props => props.percentage <= 50 ? 0 : (props.percentage - 50) * 1.8}deg);
      background: #FFA500;
    }

    &:nth-child(2) {
      left: 50%;
      background: ${props => props.percentage <= 50 ? "transparent" : "#FFA500"};
    }
  }
`;

