import React from 'react';
import styled from 'styled-components';
import { Card, CardHeader, CardContent, Typography, LinearProgress } from '@mui/material';
import Level from "./Level"

const SkillsContainer = styled.div`
  display: flex;
  flex-wrap: wrap;
  justify-content: space-around;
`;

const StyledCard = styled(Card)`
  && {
    max-width: 645px;
    margin: 16px;
    width: 500px;

    background: rgba(255, 255, 255, 0.02);
    padding: 10px; // You can adjust this as needed
    border-radius: 5px;
    color: #BDC7C9;

    &:hover {
      backdrop-filter: blur(10px);
      background-color: rgba(255, 255, 255, 0.1);  // Increase the alpha value for a clearer background
    }
  }
`;

const Title = styled.div`
  text-align: center;
  text-transform: uppercase;
  color: #03C988;
  font-weight: bold;
  font-size: 1.5em;
  padding: 8px 0;
`;

const LogoContainer = styled.div`
  display: flex;
  justify-content: center;
  img {
    width: 50px;
    height: 50px;
  }
`;
const SkillName = styled.span`
  text-align: center;  // this might not have an effect on inline elements, but no harm keeping it
  color: #03C988;
  font-size: 1rem;  // You can adjust this as per your needs
  background-color: rgba(162, 255, 134, 0.2);  // This is the color A2FF86 with 20% opacity
  padding: 4px 8px;  // Adjust as per your requirement. Padding around the text will give some space.
  border-radius: 8px; 
  backdrop-filter: blur(4px);  // Apply blur effect
  font-weight: bold;  // Making the text bold
  display: inline; // Makes the background color only as wide as the text.
  margin: 10px 0;  // Optional: Provide some spacing
`;







const SkillCard = ({ title, logoSrc, skills }) => (
  <StyledCard>
    <CardHeader title={<Title>{title}</Title>} subheader={<LogoContainer><img src={logoSrc || "https://via.placeholder.com/50"} alt={title} /></LogoContainer>} />
    <CardContent>
      {skills.map(skill => (
        <div key={skill.name}>
          <Typography variant="body2" color="text.secondary">
          <SkillName>{skill.name}</SkillName>
          </Typography>
          <Level percentage={skill.level} />

        
        </div>
      ))}
    </CardContent>
  </StyledCard>
);

const Skills = () => {
  const skillsData = [
    {
      title: "Frontend",
      logoSrc: "https://img.icons8.com/color/50/000000/nodejs.png"
      ,     skills: [
        { name: "HTML", level: 90 },
        { name: "CSS", level: 85 },
        { name: "JavaScript", level: 80 },
        // More skills...
      ],
    },
    {
      title: "Backend",
      logoSrc: "https://img.icons8.com/color/50/000000/nodejs.png"
      ,
      skills: [
        { name: "Node.js", level: 80 },
        { name: "Python", level: 75 },
        // More skills...
      ],
    },
    // More skill categories...
  ];

  return (
    <SkillsContainer>
      {skillsData.map((data, index) => (
        <SkillCard key={index} {...data} />
      ))}
    </SkillsContainer>
  );
};

export default Skills;
