import React from 'react';
import styled from 'styled-components';
import TextMod from "./About"
import { GlowsTextNoAnimation } from "./About"

const Card = styled.div`
  width: 100%;
  height: 400px;
  background-color: transparent;
  border: 1px solid white;
  margin: 0; 
  overflow: auto;
  padding: 20px;
`;

const Row = styled.div`
  display: flex;
  justify-content: flex-start;
  align-items: center;
  margin-bottom: 15px;
`;

const Logo = styled.img`
  width: 40px;
  height: 40px;
  margin-right: 15px;
`;

const Header = styled.div`
  text-align: center;
  margin-bottom: 20px;
  border-bottom: 1px solid white;
`;

const Title = styled.h1`
  margin: 10px 0;
  color: white;
`;

const SkillTag = styled.span`
  background-color: #f5f5f5;
  padding: 5px 10px;
  border-radius: 3px;
  margin-right: 15px;
`;

const Description = styled.p`
  flex: 1;
  color: white;
`;

const SkillCard = ({ category }) => {
  return (
    <Card>
      <Header>
        <Logo src="https://via.placeholder.com/40" alt="Category Logo" /> {/* Change this URL to the desired logo */}
        <Title>{category.title}</Title>
      </Header>
      {category.skills.map(skill => (
        <Row key={skill.name}>
          <Logo src={skill.logoSrc} alt={skill.name} />
          <SkillTag>{skill.name}</SkillTag>
          <GlowsTextNoAnimation as="span" color="#C3EDC0" size="18px">
            <Description>{skill.description}</Description>
          </GlowsTextNoAnimation>
        </Row>
      ))}
    </Card>
  );
}

export default SkillCard;
