import React from 'react';
import styled from 'styled-components';
import { Link } from 'react-scroll';
import { SocialIcon } from 'react-social-icons';

// Import your image
import profileImage from './IMG_0178__1_-removebg-preview.jpg';

const Name = styled.h1`
  font-family: 'JetBrains Mono' SemiBold 600;
  font-weight: 700;
  font-size: 20px;
  color: #EEEEEE;
  margin-bottom: 1rem;
  margin-left: 30px;

`;

const JobTitle = styled.h2`
  font-family: 'Gruppo', sans-serif;
  font-size: 15px;
  color: #DCDCDC;
  margin-bottom: 2rem;
  margin-left: 30px;
`;

const SidebarContainer = styled.div`
  background-color: #032D3C;
  color: #DEE1E6;
  width: 350px;
  height: 100vh;
  display: flex;
  flex-direction: column;


  overflow: visible;
`;

const ProfileImage = styled.img`
  width: 200px;
  height: 200px;
  border-radius: 50%;
  object-fit: cover;
  margin-bottom: 1rem;
`;

const WholeApp = styled.div`
  transition: 0.3s;
  &:hover {
    filter: brightness(1.1) blur(5px);
  }
`;
const StyledLink = styled(Link)`
  font-family: 'Orbitron', sans-serif;
  font-weight: 600;
  font-size: 14px;
  color: #BABCBE;
  text-decoration: none;
 margin-left: 50px;
  cursor: pointer;
  position: relative;
  display: inline-block;
  margin-bottom:20px;

  span {
    content: '';
    position: absolute;
    width: 0;
    height: 2px;
    bottom: -2px;
    left: 0;
    background-color: #B7B78A;
    transition: all 0.3s ease-in-out;
  }

  &:hover {
    color: #FAEEE0;
  }

  &:hover span {
    width: 100%;
  }
`;

const SocialMediaContainer = styled.div`
  display: flex;
  justify-content: space-around;
  width: 100%;
  padding-top: 200px;

  bottom: 2rem;
`;
const NavContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  width: 100%;
`;

const Sidebar = () => {
  return (
    <WholeApp>
    <SidebarContainer>
      <ProfileImage src={profileImage} alt="Your Name" />
      <Name>SAAD KATAR</Name>
      <JobTitle>JUNIOR SOFTWARE ENGINEER</JobTitle>
      <NavContainer>
        <StyledLink to="experience" smooth={true} duration={500}>
          Experience
          <span />
        </StyledLink>
        <StyledLink to="projects" smooth={true} duration={500}>
          Projects
          <span />
        </StyledLink>
        <StyledLink to="skills" smooth={true} duration={500}>
          Skills
          <span />
        </StyledLink>
        <StyledLink to="contact" smooth={true} duration={500}>
          Contact
          <span />
        </StyledLink>
      </NavContainer>
      <SocialMediaContainer>
        <SocialIcon url="http://linkedin.com/in/yourlinkedinprofile" bgColor="#DEE1E6" />
        <SocialIcon url="http://github.com/yourgithubprofile" bgColor="#DEE1E6" />
        <SocialIcon url="http://twitter.com/yourtwitterhandle" bgColor="#DEE1E6" />
      </SocialMediaContainer>
    </SidebarContainer>
    </WholeApp>
  );
};

export default Sidebar;
