import React from 'react';
import styled ,  {keyframes}from 'styled-components';
import { Card, CardHeader, CardContent, Typography, LinearProgress } from '@mui/material';
import Level from "./Level"

const upDownAnimation = keyframes`
  0% { transform: translateY(0); }
  50% { transform: translateY(-10px); }
  100% { transform: translateY(0); }
`;
const SkillsContainer = styled.div`
  display: flex;
  flex-wrap: nowrap;  // Ensure items don't wrap to next line
  overflow-x: auto;
  white-space: nowrap;
  flex-wrap: wrap;
  margin-top:100px;
  margin-bottom: 100px;
  margin-left:2px;
  background: linear-gradient(to right,#0B666A,  #14C38E);  // default colors are white and black
  animation: ${upDownAnimation} 2s infinite;  // Adjust the duration (2s here) as needed

`;


const StyledCard = styled(Card)`
  && {
    max-width: 645px;
    margin: 6px;
    width: 500px;

    background: #2C3333;
    border-radius: 5px;
    color: #BDC7C9;

    &:hover {
      backdrop-filter: blur(10px);
      background-color: rgba(255, 255, 255, 0.1);  // Increase the alpha value for a clearer background
    }
  }
`;

const Title = styled.div`
  text-align: center;
  text-transform: uppercase;
  color: #03C988;
  font-weight: bold;
  font-size: 1.5em;
  padding: 8px 0;
`;

const LogoContainer = styled.div`
  display: flex;
  justify-content: center;
  img {
    width: 50px;
    height: 50px;
  }
`;
const SkillName = styled.span`
  text-align: center;  // this might not have an effect on inline elements, but no harm keeping it
  color: #03C988;
  font-size: 1rem;  // You can adjust this as per your needs
  background-color: rgba(162, 255, 134, 0.2);  // This is the color A2FF86 with 20% opacity
  padding: 4px 8px;  // Adjust as per your requirement. Padding around the text will give some space.
  border-radius: 8px; 
  backdrop-filter: blur(4px);  // Apply blur effect
  font-weight: bold;  // Making the text bold
  display: inline; // Makes the background color only as wide as the text.
  margin: 10px 0;  // Optional: Provide some spacing
`;

const SkillsRow = styled.div`
  display: flex;
  justify-content: space-around;
`;

const SkillColumn = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  margin: 10px;
`;





const SkillCard = ({ title, logoSrc, skills }) => (
  <StyledCard  startColor="#092A35" endColor="#265A5C">
    <CardHeader title={<Title>{title}</Title>}   />
    <CardContent>
      <SkillsRow>
        {skills.map(skill => (
          <SkillColumn key={skill.name}>
            <LogoContainer>
              <img src={logoSrc || "https://via.placeholder.com/50"} alt={title} />
            </LogoContainer>
            <Typography variant="body2" color="text.secondary">
              <SkillName>{skill.name}</SkillName>
            </Typography>
            <Level percentage={skill.level} />
          </SkillColumn>
        ))}
      </SkillsRow>
    </CardContent>
  </StyledCard>
);

const Skills = () => {
  const skillsData = [
    {
      title: "Computer Science Fundamentals",
      logoSrc: "https://img.icons8.com/color/50/000000/computer.png",
      skills: [
        { name: "Algorithms", logoSrc: "https://img.icons8.com/color/50/000000/algorithm.png" },
        { name: "Data Structures", logoSrc: "https://img.icons8.com/color/50/000000/data-configuration.png" },
        { name: "Graph Theory", logoSrc: "https://img.icons8.com/color/50/000000/graph-theory.png" }, // Placeholder logo
        { name: "OOP", logoSrc: "https://img.icons8.com/color/50/000000/oop.png" },
        { name: "Design Patterns", logoSrc: "https://img.icons8.com/color/50/000000/blueprint.png" }  // Placeholder logo
      ]
    },
    {
      title: "Web & Mobile Development",
      logoSrc: "https://img.icons8.com/color/50/000000/web-development.png",
      skills: [
        { name: "React", logoSrc: "https://img.icons8.com/color/50/000000/react-native.png" },
        { name: "ASP.NET", logoSrc: "https://img.icons8.com/color/50/000000/asp.png" },
        { name: "Flutter", logoSrc: "https://img.icons8.com/color/50/000000/flutter.png" },
        { name: "JavaScript", logoSrc: "https://img.icons8.com/color/50/000000/javascript.png" },
        { name: "HTML/CSS", logoSrc: "https://img.icons8.com/color/50/000000/html-5--v1.png" }
      ]
    },
    {
      title: "Artificial Intelligence & Big Data",
      logoSrc: "https://img.icons8.com/color/50/000000/artificial-intelligence.png",
      skills: [
        { name: "Neural Networks", logoSrc: "https://img.icons8.com/color/50/000000/neural-network.png" },
        { name: "Computer Vision", logoSrc: "https://img.icons8.com/color/50/000000/cctv-camera.png" },
        { name: "Big Data with Hadoop", logoSrc: "https://img.icons8.com/color/50/000000/hadoop.png" },
        { name: "IoT Concepts", logoSrc: "https://img.icons8.com/color/50/000000/internet-of-things.png" },
        { name: "Power BI", logoSrc: "https://img.icons8.com/color/50/000000/power-bi.png" }
      ]
    },
    {
      title: "Cloud, DevOps & Software Architecture",
      logoSrc: "https://img.icons8.com/color/50/000000/cloud-computing.png",
      skills: [
        { name: "AWS", logoSrc: "https://img.icons8.com/color/50/000000/amazon-web-services.png" },
        { name: "Docker", logoSrc: "https://img.icons8.com/color/50/000000/docker.png" },
        { name: "DevOps", logoSrc: "https://img.icons8.com/color/50/000000/devops.png" }, // Placeholder logo
        { name: "Agile/Scrum", logoSrc: "https://img.icons8.com/color/50/000000/agile.png" },
        { name: "UML", logoSrc: "https://img.icons8.com/color/50/000000/uml.png" }
      ]
    },
    {
      title: "Programming Languages & ERP Systems",
      logoSrc: "https://img.icons8.com/color/50/000000/coding.png",
      skills: [
        { name: "Java", logoSrc: "https://img.icons8.com/color/50/000000/java-coffee-cup-logo.png" },
        { name: "Python", logoSrc: "https://img.icons8.com/color/50/000000/python.png" },
        { name: "C/C++", logoSrc: "https://img.icons8.com/color/50/000000/c-programming.png" },
        { name: "C#", logoSrc: "https://img.icons8.com/color/50/000000/c-sharp-logo.png" },
        { name: "ERP Systems", logoSrc: "https://img.icons8.com/color/50/000000/overview-pages-3.png" }  // Placeholder logo
      ]
    },
    {
    title: "Databases & Storage",
    logoSrc: "https://img.icons8.com/color/50/000000/database.png",
    skills: [
      { name: "SQL", logoSrc: "https://img.icons8.com/color/50/000000/sql.png" },
      { name: "Firebase", logoSrc: "https://img.icons8.com/color/50/000000/firebase.png" },
      { name: "MongoDB", logoSrc: "https://img.icons8.com/color/50/000000/mongodb.png" },
      { name: "PostgreSQL", logoSrc: "https://img.icons8.com/color/50/000000/postgreesql.png" },
      { name: "Redis", logoSrc: "https://img.icons8.com/color/50/000000/redis.png" }
    ]
  },
  {
    title: "Operating Systems & Platforms",
    logoSrc: "https://img.icons8.com/color/50/000000/operating-system.png",
    skills: [
      { name: "Linux", logoSrc: "https://img.icons8.com/color/50/000000/linux.png" },
      { name: "Windows", logoSrc: "https://img.icons8.com/color/50/000000/windows-10.png" },
      { name: "Android", logoSrc: "https://img.icons8.com/color/50/000000/android-os.png" },
      { name: "iOS", logoSrc: "https://img.icons8.com/color/50/000000/ios-logo.png" },
      { name: "Docker", logoSrc: "https://img.icons8.com/color/50/000000/docker.png" }
    ]
  },
  {
    title: "Frameworks & Libraries",
    logoSrc: "https://img.icons8.com/color/50/000000/framework.png", // Placeholder logo
    skills: [
      { name: "Spring Boot", logoSrc: "https://img.icons8.com/color/50/000000/spring-logo.png" },
      { name: "Node.js", logoSrc: "https://img.icons8.com/color/50/000000/nodejs.png" },
      { name: "Express.js", logoSrc: "https://img.icons8.com/color/50/000000/express.png" }, // Placeholder logo
      { name: "Vue.js", logoSrc: "https://img.icons8.com/color/50/000000/vue-js.png" },
      { name: "Angular", logoSrc: "https://img.icons8.com/color/50/000000/angularjs.png" }
    ]
  },
  {
    title: "Soft Skills & Leadership",
    logoSrc: "https://img.icons8.com/color/50/000000/groups.png", 
    skills: [
      { name: "Problem Solving", logoSrc: "https://img.icons8.com/color/50/000000/brain.png" },
      { name: "Teamwork", logoSrc: "https://img.icons8.com/color/50/000000/team.png" },
      { name: "Leadership", logoSrc: "https://img.icons8.com/color/50/000000/conference-call.png" },
      { name: "Communication", logoSrc: "https://img.icons8.com/color/50/000000/communication.png" },
      { name: "Creativity", logoSrc: "https://img.icons8.com/color/50/000000/creative.png" }
    ]
  }
  ];
  
  ``
  

  return (
    <SkillsContainer>
      {skillsData.map((data, index) => (
        <SkillCard key={index} {...data} />
      ))}
    </SkillsContainer>
  );
};

export default Skills;
