import React from 'react';
import styled from 'styled-components';
import TextMod from "./About"
import {GlowsTextNoAnimation} from "./About"

const Card = styled.div`
  width: 100%;
  max-width: 800px;
  height:400px;
  background-color: #333;
  border-radius: 5px;
  box-shadow: 0 4px 8px rgba(0,0,0,0.1);
  margin-bottom: 20px;
  overflow: auto;
  padding: 20px;
`;

const Row = styled.div`
  display: flex;
  justify-content: flex-start;
  align-items: center;
  margin-bottom: 15px;
`;

const Logo = styled.img`
  width: 40px;
  height: 40px;
  margin-right: 15px;
`;

const SkillTag = styled.span`
  background-color: #f5f5f5;
  padding: 5px 10px;
  border-radius: 3px;
  margin-right: 15px;
`;

const Description = styled.p`
  flex: 1;
  color:white;

`;

const SkillCard = ({ category }) => {
  return (
    <Card>
      {category.skills.map(skill => (
        <Row key={skill.name}>
          <Logo src={skill.logoSrc} alt={skill.name} />
          <SkillTag>{skill.name}</SkillTag>
          <GlowsTextNoAnimation as="span" color="#C3EDC0" size="14px"> <Description>{skill.description}</Description></GlowsTextNoAnimation> 

         
        </Row>
      ))}
    </Card>
  );
}

export default SkillCard;
