import React from 'react';
import styled, { keyframes } from 'styled-components';

const slideInRight = keyframes`
  0% {
    transform: translateX(100%);
    opacity: 0;
  }
  100% {
    transform: translateX(0);
    opacity: 1;
  }
`;

const ExperienceContainer = styled.div`
  width: 100%;
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  animation: 1s ${slideInRight} ease-out;
  
  h1 {
    color: #3498db;
    margin-bottom: 1rem;
  }

  .timeline {
    position: relative;
    max-width: 600px;
    margin: 0 auto;
    .timeline-item {
      padding: 10px 40px;
      position: relative;
      background: #fff;
      border-radius: 6px;
      &:after {
        content: "";
        position: absolute;
        width: 20px;
        height: 20px;
        right: -20px;
        background: #3498db;
        border-radius: 50%;
        top: 15px;
      }
      &:nth-child(2n+1) {
        align-self: flex-end;
        &:after {
          right: auto;
          left: -20px;
        }
      }
      h4 {
        font-weight: bold;
        color: #3498db;
      }
      span {
        font-size: 0.75rem;
        margin-bottom: 0.5rem;
        display: block;
        color: #888;
      }
      p {
        font-size: 0.85rem;
      }
    }
  }
`;

const Experience = () => {
  return (
    <ExperienceContainer>
      <h1>Experience</h1>
      <div className="timeline">
        <div className="timeline-item">
          <h4>Software Engineer at Google</h4>
          <span>Jun 2022 - Present</span>
          <p>Worked on developing new features and maintaining Google Search.</p>
        </div>
        <div className="timeline-item">
          <h4>Software Engineer Intern at Microsoft</h4>
          <span>May 2021 - Aug 2021</span>
          <p>Worked on the Microsoft Teams team, developing new features and fixing bugs.</p>
        </div>
      </div>
    </ExperienceContainer>
  );
};

export default Experience;
