import React from 'react';
import styled from 'styled-components';

const ModalContainer = styled.div`
  position: fixed;
  z-index: 999;
  left: 0;
  top: 0;
  width: 100%;
  height: 100%;
  background: rgba(0, 0, 0, 0.5);
  display: flex;
  justify-content: center;
  align-items: center;
`;

const ModalContent = styled.div`
  width: 80%;
  max-width: 500px;
  background: white;
  padding: 2rem;
  border-radius: 10px;
  box-shadow: 0px 5px 20px rgba(0, 0, 0, 0.5);
  position: relative;
`;

const CloseButton = styled.button`
  position: absolute;
  right: 10px;
  top: 10px;
  border: none;
  background: transparent;
  font-size: 1.5rem;
  cursor: pointer;
`;

const SkillModal = ({ closeModal }) => {
  return (
    <ModalContainer>
      <ModalContent>
        <CloseButton onClick={closeModal}>&times;</CloseButton>
        {/* Your content here */}
      </ModalContent>
    </ModalContainer>
  );
};

export default SkillModal;
