import React from 'react';
import styled, { keyframes } from 'styled-components';

const slideInRight = keyframes`
  0% {
    transform: translateX(100%);
    opacity: 0;
  }
  100% {
    transform: translateX(0);
    opacity: 1;
  }
`;

const ExperienceContainer = styled.div`
width: 100%;
display: flex;
  flex-direction: column;
  align-items: center;
  animation: 1s ${slideInRight} ease-out;
  background: rgba(255, 255, 255, 0.05);
  
  h1 {
    color: #90C8AC;
    margin-bottom: 1rem;
    width: 100%;
    text-align: left;
  }
`;

const Card = styled.div`
  background: rgba(255, 255, 255, 0.1);  // Semi-transparent white
  border-radius: 10px;
  padding: 1rem;
  margin-bottom: 1rem;
  width: 100%;  // Takes the full width of container

  h4 {
    width: 100%;  // Takes the full width of container

    font-weight: bold;
    color: #3498db;
    margin-bottom: 0.5rem;
  }

  span {
    width: 100%;  // Takes the full width of container

    font-size: 0.75rem;
    margin-bottom: 0.5rem;
    display: block;
    color: #888;
  }

  p {
    width: 100%;  // Takes the full width of container

    font-size: 0.85rem;
  }
`;

const Experience = () => {
  const experiences = [
    {
      title: "Software Engineer at Google",
      duration: "Jun 2022 - Present",
      description: "Worked on developing new features and maintaining Google Search."
    },
    {
      title: "Software Engineer Intern at Microsoft",
      duration: "May 2021 - Aug 2021",
      description: "Worked on the Microsoft Teams team, developing new features and fixinxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxg bugs."
    }
    // Add more experiences here
  ];

  return (
    <ExperienceContainer>
      <h1>Experience</h1>
      {experiences.map((exp, index) => (
        <Card key={index} >
          <h4>{exp.title}</h4>
          <span>{exp.duration}</span>
          <p>{exp.description}</p>
        </Card>
      ))}
    </ExperienceContainer>
  );
};

export default Experience;
