import React from 'react';
import styled from 'styled-components';
import { Card, CardHeader, CardContent, Typography, LinearProgress } from '@mui/material';

const SkillsContainer = styled.div`
  display: flex;
  flex-wrap: wrap;
  justify-content: space-around;
`;

const SkillCard = ({ title, logoSrc, skills }) => (
  <Card sx={{ maxWidth: 645, m: 2 }}>
    <CardHeader title={title} subheader={<img src={logoSrc} alt={title} />} />
    <CardContent>
      {skills.map(skill => (
        <div key={skill.name}>
          <Typography variant="body2" color="text.secondary">
            {skill.name}
          </Typography>
          <LinearProgress variant="determinate" value={skill.level} />
        </div>
      ))}
    </CardContent>
  </Card>
);

const Skills = () => {
  const skillsData = [
    {
      title: "Frontend",
      logoSrc: "frontend_logo.png",
      skills: [
        { name: "HTML", level: 90 },
        { name: "CSS", level: 85 },
        { name: "JavaScript", level: 80 },
        // More skills...
      ],
    },
    {
      title: "Backend",
      logoSrc: "backend_logo.png",
      skills: [
        { name: "Node.js", level: 80 },
        { name: "Python", level: 75 },
        // More skills...
      ],
    },
    // More skill categories...
  ];

  return (
    <SkillsContainer>
      {skillsData.map((data, index) => (
        <SkillCard key={index} {...data} />
      ))}
    </SkillsContainer>
  );
};

export default Skills;
