import React from 'react';
import { Button } from '@mui/material';
import Typist from 'react-typist';
import Particles from 'react-tsparticles';
import styled, { keyframes } from 'styled-components';
import ScrollIntoView from 'react-scroll-into-view';
import profilePic from './IMG_0178__1_-removebg-preview.jpg'; // Path to your image


const fade = keyframes`
  from { opacity: 0; }
  to { opacity: 1; }
`;

const glow = keyframes`
  0% { box-shadow: 0 0 5px #3498db, 0 0 10px #3498db, 0 0 15px #3498db, 0 0 20px #3498db; }
  100% { box-shadow: 0 0 10px #3498db, 0 0 20px #3498db, 0 0 30px #3498db, 0 0 40px #3498db; }
`;

// Define the styled components
const HomePageContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  height: 100vh;
  background: linear-gradient(135deg, #2c3e50 0%, #3498db 100%);
  color: white;
  font-family: 'Roboto', sans-serif;
  overflow: hidden;
`;

const Header = styled.header`
  position: sticky;
  top: 0;
  width: 100%;
  display: flex;
  justify-content: center;
  padding: 1rem 0;
  background: rgba(255, 255, 255, 0.1);
  backdrop-filter: blur(5px);
  z-index: 100;
  nav {
    display: flex;
    justify-content: space-between;
    width: 50%;
  }
  a {
    color: black;
    text-decoration: none;
    font-size: 1.5em;
    letter-spacing: 2px;
    transition: color 0.3s;
    &:hover {
      color: #3498db;
      transition: color 0.3s;
    }
  }
`;

const Hero = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  width: 80%;
  div {
    width: 40%;
  }
  img {
    width: 50%;
    max-height: auto;
    border-radius: 10px;

    animation: 2s ${fade} ease-in;
  }
  h1, h2, p {
    margin-bottom: 20px;
    animation: 2s ${fade} ease-in;
  }
  h1 {
    font-size: 3em;
    text-shadow: 2px 2px 4px rgba(0,0,0,0.5);
  }
  h2, p {
    font-size: 2em;
  }
`;

const StyledButton = styled(Button)`
  && {
    background: white;
    color: #3498db !important;
    text-shadow: 2px 2px 4px rgba(0,0,0,0.5);
    &:hover {
      background: #2980b9 !important;
    }
  }
`;

const Footer = styled.footer`
  position: fixed;
  bottom: 0;
  width: 100%;
  display: flex;
  justify-content: center;
  padding: 10px 0;
  background: rgba(0, 0, 0, 0.8);
  backdrop-filter: blur(5px);
`;

const HomePage = () => {
  return (
    <HomePageContainer>
      <Particles
        id="tsparticles"
        options={{
          particles: {
            number: { value: 50 },
            size: { value: 3 },
          },
          interactivity: {
            events: {
              onHover: {
                enable: true,
                mode: 'repulse',
              },
            },
          },
        }}
      />
      <Header>
        <nav>
          <ScrollIntoView selector="#about"><a href="#about">About</a></ScrollIntoView>
          <ScrollIntoView selector="#skills"><a href="#skills">Skills</a></ScrollIntoView>
          <ScrollIntoView selector="#projects"><a href="#projects">Projects</a></ScrollIntoView>
        </nav>
      </Header>
      <Hero>
        <div>
          <h1>
            <Typist>Your Name</Typist>
          </h1>
          <h2>
            <Typist>Junior Software Engineer</Typist>
          </h2>
          <p>
            <Typist>A short, catchy phrase that describes you</Typist>
          </p>
          <StyledButton variant="contained">See My Projects</StyledButton>
        </div>
        <img src={profilePic} alt="Your professional avatar" />
      </Hero>
      <Footer>
        <p>Made with ❤️ using React</p>
      </Footer>
    </HomePageContainer>
  );
};

export default HomePage;
