import React from 'react';
import styled from 'styled-components';

const Box = styled.div`
  width: 200px;
  height: 100px;
  margin: 10px;
`;

// Linear Gradient Component
const LinearGradientBox = styled(Box)`
  background: linear-gradient(${props => props.direction || 'to bottom'}, ${props => props.startColor || 'red'}, ${props => props.endColor || 'yellow'});
`;

// Radial Gradient Component
const RadialGradientBox = styled(Box)`
  background: radial-gradient(${props => props.shape || 'circle'}, ${props => props.startColor || 'lightblue'}, ${props => props.endColor || 'blue'});
`;

// Repeating Linear Gradient Component
const RepeatingLinearGradientBox = styled(Box)`
  background: repeating-linear-gradient(${props => props.angle || '45deg'}, ${props => props.startColor || 'yellow'}, ${props => props.startColor || 'yellow'} ${props => props.startStop || '10px'}, ${props => props.endColor || 'red'} ${props => props.startStop || '10px'}, ${props => props.endColor || 'red'} ${props => props.endStop || '20px'});
`;

// Repeating Radial Gradient Component
const RepeatingRadialGradientBox = styled(Box)`
  background: repeating-radial-gradient(${props => props.shape || 'circle'}, ${props => props.startColor || 'lightgreen'}, ${props => props.startColor || 'green'} ${props => props.startStop || '30px'}, ${props => props.endColor || 'lightgreen'} ${props => props.startStop || '30px'}, ${props => props.endColor || 'lightgreen'} ${props => props.endStop || '60px'});
`;

const GradientDisplay = () => {
  return (
    <div>
      <h2>Linear Gradient</h2>
      <LinearGradientBox direction="to right" startColor="pink" endColor="purple" />

      <h2>Radial Gradient</h2>
      <RadialGradientBox shape="circle" startColor="orange" endColor="red" />
      
      <h2>Repeating Linear Gradient</h2>
      <RepeatingLinearGradientBox angle="45deg" startColor="cyan" endColor="blue" startStop="5px" endStop="10px" />
      
      <h2>Repeating Radial Gradient</h2>
      <RepeatingRadialGradientBox shape="circle" startColor="yellow" endColor="green" startStop="10px" endStop="20px" />
    </div>
  );
}

export default GradientDisplay;
