import React from 'react';
import styled, { keyframes } from 'styled-components';
import {CircleExpand,FloatingElement} from "./StyledComp"
import profileImage from './IMG_0178__1_-removebg-preview.jpg';
import { GlowsText } from './OldPortfolio';

const fadeIn = keyframes`
  0% { opacity: 0; }
  100% { opacity: 1; }
`;

const Container = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: flex-start;
`;

const ImageWrapper = styled(FloatingElement)`
  width: 30%;  // Adjust this width as necessary
`;

const AboutContainer = styled.div`
  margin: 2rem 0;
  margin-top: 0;
  width: 60%;
  border-radius: 10px;
  padding: 1rem;
  animation: 2s ${fadeIn} ease-in;

  h2 {
    color: #0693e3;
    font-size: 2.5em;
  }

  p {
    margin-right: 100px;
    transition: all 0.3s ease-in-out;
    padding: 10px;
    border-radius: 5px;
    color: #BDC7C9;
    line-height: 1.6;
    font-size: 1.2em;
    font-family: 'Roboto Slab';
    font-weight: 400;  
  }
  
  p:hover {
    backdrop-filter: blur(10px);
    background-color: rgba(255, 255, 255, 0.005);
  }
`;
const FloatingWrapper = styled.div`
  position: relative;  // This is to contextually position the ProfileImage
  display: flex;
  justify-content: center;  // To center the ProfileImage horizontally
  align-items: center;     // To center the ProfileImage vertically
  width: 30%;  // Adjust this width as necessary
`;

const ProfileImage = styled.img`
  position: absolute;  // Makes it overlay on top of its parent
  top: 50%;  // Center it vertically
  left: 50%; // Center it horizontally
  transform: translate(-50%, -50%);  // Pull the image back to its center
  width: 450px;
  height: 450px;
  border-radius: 50%;
  object-fit: cover;
`;
const TextMod = styled.div`
  font-family: ${props => props.font || 'Roboto'};
  font-weight: ${props => props.weight || '400'};
  font-size: ${props => props.size || '16px'};
  color: ${props => props.color || 'white'};
`;


const About = () => {
  return (
    <Container>
    <AboutContainer>
    <TextMod font="Orbitron" weight="600" size="36px">
      I Make Software
    </TextMod>
    
    <TextMod font="Roboto Slab" weight="500" size="24px" color="#F8F6F4">
      Dedicated to designing, developing, modern software solutions with a focus on continuous improvement and learning. I'm skilled in frontend tools like React and Javascript, as well as backend technologies such as&nbsp;
      <GlowsText as="span" color="#C3EDC0" size="27px">ASP.NET</GlowsText>&nbsp;and&nbsp;
      <GlowsText as="span" color="#C3EDC0" size="27px">C#</GlowsText>.&nbsp;I design robust software architectures and turn system requirements into real solutions. Using principles like&nbsp;
      <TextMod as="span" size="27px" weight="900">OOP</TextMod>&nbsp;and&nbsp;
      <TextMod as="span" size="27px" weight="900">design patterns</TextMod>, my code is&nbsp;
      <TextMod as="span" size="27px" weight="900" color="#F8FFDB">clear</TextMod>&nbsp;and&nbsp;
      <TextMod as="span" size="27px" weight="900" color="#F8FFDB">efficient</TextMod>. I embrace challenges, always aiming for growth and improvement in&nbsp;
      <TextMod as="span" size="27px" weight="900" color="#F8FFDB">agile</TextMod>&nbsp;settings.
    </TextMod>
  </AboutContainer>
    
    <FloatingWrapper>
      <FloatingElement>
        <CircleExpand>
          {/* Your Image or any content here */}
        </CircleExpand>
      </FloatingElement>
      <ProfileImage src={profileImage} alt="Your Name" />
    </FloatingWrapper>
  </Container>
);
};

export default About;
