import React from 'react';
import styled, { keyframes } from 'styled-components';

const fadeIn = keyframes`
  0% { opacity: 0; }
  100% { opacity: 1; }
`;

const AboutContainer = styled.div`
  margin: 2rem auto;
  
  width: 100%;
  margin-right:50px;
 
  border-radius: 10px;
  padding: 1rem;
 
  animation: 2s ${fadeIn} ease-in;

  h2 {
    color: #0693e3;
    font-size: 2.5em;
  }

  p {
    margin-right:100px;

    transition: all 0.3s ease-in-out;
    background: rgba(255, 255, 255, 0.02);
    padding: 10px; // You can adjust this as needed
    border-radius: 5px;
    color: #BDC7C9  ;
    line-height: 1.6;
    font-size: 1.2em;
    font-family: 'Roboto Slab';
    font-weight: 400;  
    
  }
  p:hover {
   
    backdrop-filter: blur(10px);

    background-color: rgba(255, 255, 255, 0.1);  // Increase the alpha value for a clearer background
  }
`;

const About = () => {
  return (
    <AboutContainer>
      <h2>About Me</h2>
      <p>
        Hello! I'm John Do, a software engineer specializing in building (and occasionally designing) exceptional websites, applications, and everything in between. My goal is to always build products that provide pixel-perfect, performant experiences.
      </p>
    </AboutContainer>
  );
};

export default About;
