import React, { useState } from 'react';
import OldPortfolio from './OldPortfolio'
import styled from 'styled-components';

import  App from './App';

const MainContainer = styled.div`
  display: flex;
  height: 100vh; // take full viewport height
  overflow: hidden; // hide any overflow caused by children
  background:  rgb(0, 0, 34);
   ::-webkit-scrollbar {
    display: none;
  }

  // Hide scrollbar for Firefox
  body {
    scrollbar-width: none;
    -ms-overflow-style: none;  // IE and Edge
  }
  

`;

const Content = styled.div`
  flex-grow: 1;
 // Hide scrollbar for Chrome, Safari and Opera
  &::-webkit-scrollbar {
    display: none;
  }

  // Hide scrollbar for Firefox
  scrollbar-width: none;

  overflow-y: auto;
  height: 100%; // take full height of parent
`;
const SidebarWrapper = styled.div`
  overflow: hidden;
  margin:0;
  padding:0;
  background: green;
  
`;

const ContentComponent = () => {
  const [scrollPercentage, setScrollPercentage] = useState(0);

  const handleScroll = (e) => {
    const { scrollTop, scrollHeight, clientHeight } = e.currentTarget;
    setScrollPercentage(scrollTop / (scrollHeight - clientHeight));
  };

  // Interpolate background color
  const red = Math.round(24 + 231 * scrollPercentage);
  const green = Math.round(44 + 193 * scrollPercentage);
  const blue = Math.round(37 + 100 * scrollPercentage);
  const backgroundColor = `rgb(${red}, ${green}, ${blue})`;

  return (
    <MainContainer>
    <App backgroundColor={backgroundColor} scrollPercentage={scrollPercentage} />
    <SidebarWrapper scrollPercentage={scrollPercentage}>
    </SidebarWrapper>
    <Content onScroll={handleScroll}>
      <OldPortfolio/>
    </Content>
  </MainContainer>
  );
};

export default ContentComponent;
const GlobalStyle = createGlobalStyle`
  // Hide scrollbar for Chrome, Safari and Opera
  ::-webkit-scrollbar {
    display: none;
  }

  // Hide scrollbar for Firefox
  body {
    scrollbar-width: none;
    -ms-overflow-style: none;  // IE and Edge
  }

  html, body {
    overflow-x: hidden; // This hides horizontal scrollbar.
  }
`;
