import React from 'react';
import styled from 'styled-components';
import { Card, CardHeader, CardContent, Typography, LinearProgress } from '@mui/material';

const SkillsContainer = styled.div`
  display: flex;
  flex-wrap: wrap;
  justify-content: space-around;
`;

const StyledCard = styled(Card)`
  && {
    max-width: 645px;
    margin: 16px;
    width: 500px;

    background: rgba(255, 255, 255, 0.02);
    padding: 10px; // You can adjust this as needed
    border-radius: 5px;
    color: #BDC7C9;

    &:hover {
      backdrop-filter: blur(10px);
      background-color: rgba(255, 255, 255, 0.1);  // Increase the alpha value for a clearer background
    }
  }
`;

const Title = styled.div`
  text-align: center;
  text-transform: uppercase;
  color: #03C988;
  font-weight: bold;
  font-size: 1.5em;
  padding: 8px 0;
`;

const LogoContainer = styled.div`
  display: flex;
  justify-content: center;
  img {
    width: 50px;
    height: 50px;
  }
`;

const Level = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  color: #03C988;
  font-size: 0.8em;
  background-color: #46C2CB;
  border-radius: 5px;
  padding: 5px;
  margin-top: 5px;
`;

const SkillCard = ({ title, logoSrc, skills }) => (
  <StyledCard>
    <CardHeader title={<Title>{title}</Title>} subheader={<LogoContainer><img src={logoSrc || "https://via.placeholder.com/50"} alt={title} /></LogoContainer>} />
    <CardContent>
      {skills.map(skill => (
        <div key={skill.name}>
          <Typography variant="body2" color="text.secondary">
            {skill.name}
          </Typography>
          <LinearProgress color="#03C988" variant="determinate" value={skill.level} />
          <Level>{skill.level}%</Level>
        </div>
      ))}
    </CardContent>
  </StyledCard>
);

const Skills = () => {
  const skillsData = [
    {
      title: "Frontend",
      logoSrc: "frontend_logo.png", // Replace with your logo URL
      skills: [
        { name: "HTML", level: 90 },
        { name: "CSS", level: 85 },
        { name: "JavaScript", level: 80 },
        // More skills...
      ],
    },
    {
      title: "Backend",
      logoSrc: "backend_logo.png", // Replace with your logo URL
      skills: [
        { name: "Node.js", level: 80 },
        { name: "Python", level: 75 },
        // More skills...
      ],
    },
    // More skill categories...
  ];

  return (
    <SkillsContainer>
      {skillsData.map((data, index) => (
        <SkillCard key={index} {...data} />
      ))}
    </SkillsContainer>
  );
};

export default Skills;
