import React from 'react';
import styled from 'styled-components';
import TextMod from "./About"
import { GlowsTextNoAnimation } from "./About"

const CardWraper = styled.div`
  ...
  border-right: 1px solid white;
  
  &:first-child {
    border-right: none;  // Ensures the last card doesn't have a right border
  }
`;
const Card = styled.div`
  width: 100%;
  height: 400px;
  background-color: transparent;
  border: 2px solid #00DFA2;
  margin: 0; 
  padding: 20px;

  display: flex;
  flex-direction: column;
  &:nth-child(2n-1) {  // Targeting all odd children (1st, 3rd, 5th, ...)
    border-right: none;
  }
`;

const ScrollableContent = styled.div`
  overflow: auto;
  flex: 1;
`;

const Row = styled.div`
  display: flex;
  justify-content: flex-start;
  align-items: center;
  margin-bottom: 15px;
`;

const Logo = styled.img`
  width: 40px;
  height: 40px;
  margin-right: 15px;
`;

const Header = styled.div`
  text-align: center;
  color:#FEFCF3;
  margin-bottom: 20px;
  border-bottom: 3px solid #5C8374;
  padding: 10px 0;
`;

const Title = styled.h1`
  margin: 10px 0;
  color: #FEFCF3;

`;

const SkillTag = styled.span`
  background-color: #f5f5f5;
  padding: 5px 10px;
  border-radius: 3px;
  margin-right: 15px;
`;

const Description = styled.p`
  flex: 1;
  color: white;
  font-family:"Roboto Slab";
  weight:800;
  font-size:20px;

`;

const SkillCard = ({ category }) => {
  return (

    <Card>
      <Header>
        <Logo src="https://via.placeholder.com/40" alt="Category Logo" />
        <Title>{category.title}</Title>
      </Header>
      <ScrollableContent>
        {category.skills.map(skill => (
          <Row key={skill.name}>
            <Logo src={skill.logoSrc} alt={skill.name} />
            <SkillTag>{skill.name}</SkillTag>
              <Description>{skill.description}</Description>
            
          </Row>
        ))}
      </ScrollableContent>
    </Card>
  );
}

export default SkillCard;
