import React from 'react';
import { Button } from '@mui/material';
import Typist from 'react-typist';
import Particles from 'react-tsparticles';
import styled from 'styled-components';

// Define the styled components
const HomePageContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  height: 100vh;
  background: linear-gradient(45deg, #3498db 30%, #2980b9 90%);
  color: white;
  font-family: 'Roboto', sans-serif;
`;

const Header = styled.header`
  position: absolute;
  top: 0;
  width: 100%;
  display: flex;
  justify-content: space-evenly;
  padding: 10px 0;
  background: rgba(250, 250, 250, 0.8);
  backdrop-filter: blur(5px);
  a {
    color: white;
    text-decoration: none;
    font-size: 1.5em;
    letter-spacing: 2px;
    margin: 0 10px;
  }
`;

const Hero = styled.div`
  display: flex;
  justify-content: space-around;
  align-items: center;
  width: 100%;
  h1, h2, p {
    margin-bottom: 20px;
  }
  h1 {
    font-size: 3em;
  }
  h2, p {
    font-size: 2em;
  }
  img {
    max-width: 50%;
    height: auto;
    border-radius: 10px;
  }
`;

const StyledButton = styled(Button)`
  && {
    background: white;
    color: #3498db !important;
    &:hover {
      background: #2980b9 !important;
    }
  }
`;

const Footer = styled.footer`
  position: absolute;
  bottom: 0;
  width: 100%;
  display: flex;
  justify-content: center;
  padding: 10px 0;
  background: rgba(0, 0, 0, 0.8);
  backdrop-filter: blur(5px);
`;

const HomePage = () => {
  return (
    <HomePageContainer>
      <Particles
        id="tsparticles"
        options={{
          particles: {
            number: { value: 50 },
            size: { value: 3 },
          },
          interactivity: {
            events: {
              onHover: {
                enable: true,
                mode: 'repulse',
              },
            },
          },
        }}
      />
      <Header>
        <nav>
          <a href="#about">About</a>
          <a href="#skills">Skills</a>
          <a href="#projects">Projects</a>
        </nav>
      </Header>
      <Hero>
        <div>
          <h1>
            <Typist>Your Name</Typist>
          </h1>
          <h2>
            <Typist>Junior Software Engineer</Typist>
          </h2>
          <p>
            <Typist>A short, catchy phrase that describes you</Typist>
          </p>
          <StyledButton variant="contained">See My Projects</StyledButton>
        </div>
        <img src="your-image.jpg" alt="Your professional avatar" />
      </Hero>
      <Footer>
        <p>Made with ❤️ using React</p>
      </Footer>
    </HomePageContainer>
  );
};

export default HomePage;
