import React from 'react';
import { Card, CardContent, Typography } from '@mui/material';
import styled from "styled-components"
import { color } from 'framer-motion';

const Style = styled.div`
&& {
  max-width: 645px;
  margin: 16px;
  width: 500px;

  background: rgba(255, 255, 255, 0.2);
  padding: 10px; // You can adjust this as needed
  border-radius: 5px;
  color: #BDC7C9;

  &:hover {
    backdrop-filter: blur(10px);
    background-color: rgba(255, 255, 255, 0.1);  // Increase the alpha value for a clearer background
  }
}
`;


const ProjectCard = ({ title, description }) => (
  <Style>
  <Card style={{ minWidth: 275, margin: '1em', transform: 'scale(1)', transition: 'transform .5s', color:"red" }}>
    <CardContent>
      <Typography variant="h5" component="div">
        {title}
      </Typography>
      <Typography variant="body2" color="text.secondary">
        {description}
      </Typography>
    </CardContent>
  </Card>
  </Style>
 
);

export default ProjectCard;
