import React from 'react';
import styled from 'styled-components';
import { Link } from 'react-scroll';
import { SocialIcon } from 'react-social-icons';

const Name = styled.h1`
  color: #EEEEEE;
  margin-bottom: 1rem;
`;

const JobTitle = styled.h2`
  color: #DCDCDC;
  margin-bottom: 2rem;
`;

const SidebarContainer = styled.div`
  background-color: #182c25;
  color: #DEE1E6;
  width: 250px;
  height: 100vh;
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 2rem;
  overflow: visible;
`;

const StyledLink = styled(Link)`
  color: #DEE1E6;
  text-decoration: none;
  margin: 0.5rem 0;
  cursor: pointer;
  position: relative;
  display: inline-block;

  ::after {
    content: '';
    position: absolute;
    height: 2px;
    bottom: 0;
    left: 0;
    background-color: #F1F3F4;
    width: 0;
    transition: all 0.3s ease-in-out;
  }

  &:hover {
    color: #F1F3F4;
  }

  &:hover::after {
    width: 100%;
  }
`;

const SocialMediaContainer = styled.div`
  display: flex;
  justify-content: space-around;
  width: 100%;
  position: absolute;
  bottom: 2rem;
`;

const Sidebar = () => {
  return (
    <SidebarContainer>
      <Name>Your Name</Name>
      <JobTitle>Your Job Title</JobTitle>
      <StyledLink to="experience" smooth={true} duration={500}>Experience</StyledLink>
      <StyledLink to="projects" smooth={true} duration={500}>Projects</StyledLink>
      <StyledLink to="skills" smooth={true} duration={500}>Skills</StyledLink>
      <StyledLink to="contact" smooth={true} duration={500}>Contact</StyledLink>
      <SocialMediaContainer>
        <SocialIcon url="http://linkedin.com/in/yourlinkedinprofile" bgColor="#DEE1E6" />
        <SocialIcon url="http://github.com/yourgithubprofile" bgColor="#DEE1E6" />
        <SocialIcon url="http://twitter.com/yourtwitterhandle" bgColor="#DEE1E6" />
      </SocialMediaContainer>
    </SidebarContainer>
  );
};

export default Sidebar;
