import React from 'react';
import styled from 'styled-components';

const Card = styled.div`
  width: 100%;
  max-width: 400px; // Adjust as necessary
  background-color: #ffffff;
  border-radius: 5px;
  box-shadow: 0 4px 8px rgba(0,0,0,0.1);
  margin-bottom: 20px;
  overflow: auto; // In case the content is longer than the card's height
  padding: 20px;
`;

const Row = styled.div`
  display: flex;
  justify-content: flex-start;
  align-items: center; // This will vertically center the content of the row
  margin-bottom: 15px; // Spacing between rows
`;

const Logo = styled.img`
  width: 40px;
  height: 40px;
  margin-right: 15px; // Spacing between the logo and the next item
`;

const SkillTag = styled.span`
  background-color: #f5f5f5;
  padding: 5px 10px;
  border-radius: 3px;
  margin-right: 15px; // Spacing between the skill tag and the next item
`;

const Description = styled.p`
  flex: 1; // This will make the description take the remaining space
`;

const SkillCard = ({ logoUrl, skillName, description }) => {
  return (
    <Card>
      <Row>
        <Logo src={logoUrl} alt={skillName} />
        <SkillTag>{skillName}</SkillTag>
        <Description>{description}</Description>
      </Row>
    </Card>
  );
}

export default SkillCard;
